class Game {
    constructor() {
        this.time = 30; // Temps de jeu
        this.manche = 1; // Numéro de la manche
        this.joueurs_en_cours = 0; // Numéro du joueur en cours
        this.nb_joueurs_en_vie = 0; // Nombre de joueurs en vie
    }
}

let game; // Variable contenant l'instance du jeu 
// Fonction à appeler pour créer une instance du jeu 
function game(){
    // Création du jeu
    game = new Game();
}
